table.data:
delete from .dbo.CodeManagement; INSERT INTO .dbo.CodeManagement ([ID], [Code], [Hidden29]) SELECT P.[MAINBOSSID], P.[EMPCODE], 0 -- Could set HIDDEN true if DEPARTMENT = 'RETIRED' ? from .dbo.EMP as P where P.MAINBOSSEXCLUDE = 0;
exec .dbo.FixCodes 50
INSERT INTO .dbo.Employee ([ID],[ContactID],[Desc],[Comment],[Hidden] ) SELECT PL.[EmployeeID], PL.[ContactID],CM.[Code], NULLIF(LTRIM(convert(varchar(max), COALESCE(P.DEPARTMENT, ' ')) + ' ' + RTRIM(convert(varchar(max), COALESCE(P.NOTES, ' ')))), ' '), CM.[Hidden30] from .dbo.EMP as P join .dbo.PersonnelLinkages as PL on P.[MAINBOSSID] = PL.ID join .dbo.CodeManagement as CM on P.[MAINBOSSID] = CM.[ID] where P.MAINBOSSEXCLUDE = 0; GO
