table.data:
delete from .dbo.CodeManagement; INSERT INTO .dbo.CodeManagement ([ID], [Code], [Hidden29]) SELECT B.[MAINBOSSID], B.[SITEID], 0 from .dbo.SITE as B where B.MAINBOSSEXCLUDE = 0;
INSERT INTO .dbo.CodeManagement ([ID], [Code], [Hidden29]) SELECT V.[MAINBOSSID], V.[VENDORID], 0 from .dbo.VENDOR as V where V.MAINBOSSEXCLUDE = 0;
exec .dbo.FixCodes 50
INSERT INTO .dbo.Location ([ID],[Desc],[Comment]) SELECT [MAINBOSSID],[DESCRIPTION], 'SITE' from .dbo.SITE where MAINBOSSEXCLUDE = 0;
INSERT INTO .dbo.PostalAddress ([ID],[Code],[Hidden],[LocationID],[Address1],[Address2],[City],[Territory],[Country],[PostalCode]) SELECT SL.PostalAddressID, CM.[Code], CM.[HIDDEN30], SL.LocationID, LEFT([ADDR1], 30), LEFT([ADDR2], 30), [CITY], [STATE], [COUNTRY], [ZIP] from .dbo.SITE as S join .dbo.SiteLinkages as SL on S.[MAINBOSSID] = SL.ID join .dbo.CodeManagement as CM on CM.[ID] = S.[MAINBOSSID] where MAINBOSSEXCLUDE = 0;
INSERT INTO .dbo.Location ([ID],[Desc],[Comment]) SELECT VL.LocationID, [NAME], 'VENDOR' from .dbo.VENDOR as V join .dbo.VendorLinkages as VL on V.[MAINBOSSID] = VL.ID;
INSERT INTO .dbo.PostalAddress ([ID],[Code],[Hidden],[LocationID],[Address1],[Address2],[City],[Territory],[Country],[PostalCode]) SELECT VL.PostalAddressID, CM.[CODE], CM.[HIDDEN30], VL.LocationID, LEFT([ADDR1], 30), LEFT([ADDR2], 30), [CITY], [STATE], [COUNTRY], [ZIP] from .dbo.VENDOR as V join .dbo.VendorLinkages as VL on V.[MAINBOSSID] = VL.ID join .dbo.CodeManagement as CM on CM.[ID] = V.[MAINBOSSID];
