table.data:
delete from .dbo.CodeManagement; insert into .dbo.CodeManagement ([ID], [Code], [Hidden29]) SELECT V.[MAINBOSSID], V.[VENDORID], 0 from .dbo.VENDOR as V where V.MAINBOSSEXCLUDE = 0;
exec .dbo.FixCodes 50
INSERT INTO .dbo.Vendor ([ID],[Code],[Desc],[Comment],[AccountNumber],[VendorCategoryID], [SalesContactID], [Hidden], [AccountsPayableCostCenterID] ) SELECT V.[MAINBOSSID],CM.[CODE],V.[NAME], V.NOTES, V.[VENDORBRANCHID], VC.ID, VL.ContactID, CM.[Hidden30], (select [AccountsPayableCostCenterID] from .dbo._DVendor) from .dbo.VENDOR as V join .dbo.VendorLinkages VL on V.[MAINBOSSID] = VL.ID left join .dbo.VendorCategories as VC on V.VENDORTYPE = VC.[CODE] join .dbo.CodeManagement as CM on V.MAINBOSSID = CM.ID
