delete from .dbo.CodeManagement; INSERT INTO .dbo.CodeManagement ([ID], [Code], [Hidden29]) SELECT P.[ID], P.[Code], COALESCE(P.[Hidden],0) from .dbo.Personnel as P where P.EXCLUDE = 0;
exec .dbo.FixCodes 50
INSERT INTO .dbo.Employee ([ID],[ContactID],[Desc],[Comment],[Hidden] ) SELECT PL.[EmployeeID], PL.[ContactID],CM.[Code],P.COMMENT, CM.[Hidden30] from .dbo.Personnel as P join .dbo.PersonnelLinkages as PL on P.[ID] = PL.ID join .dbo.CodeManagement as CM on P.[ID] = CM.[ID] where P.EXCLUDE = 0; GO
