table.data:
delete from .dbo.CodeManagement; INSERT INTO .dbo.CodeManagement ([ID], [Code], [Hidden29]) SELECT B.[ID], B.[Code], COALESCE(B.[Hidden],0) from .dbo.Buildings as B where B.EXCLUDE = 0;
INSERT INTO .dbo.CodeManagement ([ID], [Code], [Hidden29]) SELECT V.[ID], V.[Code], COALESCE(V.[Hidden],0) from .dbo.Vendors as V where V.EXCLUDE = 0;
exec .dbo.FixCodes 50
INSERT INTO .dbo.Location ([ID],[Desc],[Comment]) SELECT [ID],[Desc],'Buildings' from .dbo.Buildings where EXCLUDE = 0;
INSERT INTO .dbo.PostalAddress ([ID],[Code],[Hidden],[LocationID],[Address1],[Address2],[City],[Territory],[Country],[PostalCode]) SELECT BL.PostalAddressID, CM.[Code], CM.[HIDDEN30], BL.LocationID, [ADDRESS1], [ADDRESS2], [CITY], [TERRITORY], NULL, [PCODE] from .dbo.Buildings as B join .dbo.BuildingLinkages as BL on B.[Id] = BL.ID join .dbo.CodeManagement as CM on CM.[ID] = B.[ID] where EXCLUDE = 0;
INSERT INTO .dbo.Location ([ID],[Desc],[Comment]) SELECT VL.LocationID, [Desc], 'Vendors' from .dbo.Vendors as V join .dbo.VendorLinkages as VL on V.[Id] = VL.ID;
INSERT INTO .dbo.PostalAddress ([ID],[Code],[Hidden],[LocationID],[Address1],[Address2],[City],[Territory],[Country],[PostalCode]) SELECT VL.PostalAddressID, CM.[CODE], CM.[HIDDEN30], VL.LocationID, [ADD1], [ADD2], NULL, [ADD3], NULL, NULL from .dbo.Vendors as V join .dbo.VendorLinkages as VL on V.[Id] = VL.ID join .dbo.CodeManagement as CM on CM.[ID] = V.[ID];
